function mask_valid(mask) {
    if (typeof mask != 'string') {
        return false;
    }
    let els = mask.split(".");
    let mask_v = 0;
    mask.split(".").forEach((e, i) => {
        if (i > 3 || isNaN(e) || Number(e) > 255 || Number(e) < 0) {
            return false;
        }
        mask_v = (mask_v << 8) + Number(e);
    });
	
    mask_v = (~mask_v) & 0xffffffff;
	if((~mask_v)==0){
		return false;
	}
	return (mask_v & (mask_v + 1)) == 0;
};